                    "download": {
                        "href": "http://test-dev-feature-14.core.akeneo.com/api/rest/v1/reference-entities-media-files/8/f/2/4/8f244c7911e1e70125e224f53273583a7b3a7883_kartell_products.jpg"
                    }
