{
  "$id": "https://example.com/product",
  "$schema": "https://api.akeneo.com/mapping/product/1.0.3/schema",
  "type": "object",
  "properties": {
    "code": {
      "title": "Model ID",
      "type": "string"
    },
    "brand": {
      "title": "Brand name",
      "type": "string"
    },
    "description": {
      "title": "Product description",
      "type": "string"
    },
    "fabrics": {
      "title": "Fabrics",
      "type": "array",
      "items": {
        "type": "string"
      }
    },
    "localized_names": {
      "title": "Name with multiple locales",
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "locale": {
            "type": "string"
          },
          "value": {
            "type": "string"
          }
        },
        "required": [
          "locale",
          "value"
        ]
      }
    },
    "localized_number": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "locale": {
            "type": "string"
          },
          "value": {
            "type": "number"
          }
        },
        "required": [
          "locale",
          "value"
        ]
      }
    },
    "localized_link": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "locale": {
            "type": "string"
          },
          "value": {
            "type": "string",
            "format": "uri"
          }
        },
        "required": [
          "locale",
          "value"
        ]
      }
    },
    "localized_datetime": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "locale": {
            "type": "string"
          },
          "value": {
            "type": "string",
            "format": "date-time"
          }
        },
        "required": [
          "locale",
          "value"
        ]
      }
    },
    "localized_boolean": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "locale": {
            "type": "string"
          },
          "value": {
            "type": "boolean"
          }
        },
        "required": [
          "locale",
          "value"
        ]
      }
    },
    "variation_axes": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "code": {
            "type": "string"
          },
          "label": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "locale": {
                  "type": "string"
                },
                "value": {
                  "type": "string"
                }
              }
            }
          }
        }
      }
    },
    "variants": {
      "oneOf": [
        {
          "type": "string",
          "format": "uri"
        },
        {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "variation_axes_values": {
                "type": "array",
                "items": {
                  "type": "object",
                  "properties": {
                    "code": {
                      "type": "string"
                    },
                    "value": {
                      "oneOf": [
                        {
                          "type": [
                            "string",
                            "boolean"
                          ]
                        },
                        {
                          "type": "object",
                          "properties": {
                            "amount": {
                              "type": "number"
                            },
                            "unit": {
                              "type": "string"
                            }
                          }
                        }
                      ]
                    },
                    "label": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "locale": {
                            "type": "string"
                          },
                          "value": {
                            "type": "string"
                          }
                        }
                      }
                    }
                  }
                }
              },
              "uuid": {
                "title": "uuid",
                "type": "string"
              },
              "sku": {
                "title": "SKU",
                "type": "string"
              },
              "position": {
                "title": "Position",
                "type": "number"
              },
              "price": {
                "title": "Price",
                "type": "string"
              },
              "response_time": {
                "title": "Response time",
                "type": "string"
              },
              "localized_product_photos_media_file": {
                "title": "Photos with value per locale(links)",
                "type": "array",
                "items": {
                  "type": "object",
                  "properties": {
                    "locale": {
                      "type": "string"
                    },
                    "value": {
                      "type": "array",
                      "items": {
                        "type": "string",
                        "format": "uri"
                      }
                    }
                  },
                  "required": [
                    "locale",
                    "value"
                  ]
                }
              },
              "localized_prices": {
                "title": "Price in multiple currencies with value per locale",
                "type": "array",
                "items": {
                  "type": "object",
                  "properties": {
                    "locale": {
                      "type": "string"
                    },
                    "value": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "amount": {
                            "type": "number"
                          },
                          "currency": {
                            "type": "string"
                          }
                        },
                        "required": [
                          "amount",
                          "currency"
                        ]
                      }
                    }
                  },
                  "required": [
                    "locale",
                    "value"
                  ]
                }
              }
            },
            "required": [
              "uuid"
            ]
          }
        }
      ]
    }
  }
}

