/*
 For simplicity, we acquire a new token before each API call.
 This is not recommended to do the same in production.

 Benefit from the 1 hour validity of the access_token and leverage the refresh_token grant type.
 Explore the official PIM API documentation for details https://api.akeneo.com/documentation/authentication.html

*/

// In case an App token is defined, we use it directly
appAccessToken = pm.environment.get('app_access_token');
if (appAccessToken !== undefined) {
  pm.environment.set('bearerToken', appAccessToken);
  console.log('Used the token provided in the "app_access_token" variable instead of authenticating with the PIM.');

  return;
}

// Deal with the token endpoint
if (pm.request.url.getPath().endsWith('/api/oauth/v1/token') &&
    pm.request.method === 'POST') {
  const clientId = pm.collectionVariables.get('clientId');
  const secret = pm.collectionVariables.get('secret');

  if (clientId && secret) {
    const encodedData = btoa(clientId + ':' + secret);
    pm.request.auth = {
      type: 'basic',
      basic: [
        {key: 'username', value: clientId},
        {key: 'password', value: secret}
      ]
    };
    pm.request.headers.upsert({
      key: 'Authorization',
      value: 'Basic ' + encodedData
    });
  }

  const username = pm.collectionVariables.get('username');
  const password = pm.collectionVariables.get('password');

  if (username && password && pm.request.body && pm.request.body.raw) {
    let body = JSON.parse(pm.request.body.raw);
    body.username = username;
    body.password = password;
    pm.request.body.raw = JSON.stringify(body);
  }

  return;
}

// If the request is not to the OAuth token endpoint, we proceed with the authentication
base64ClientIdSecret = require('btoa')(pm.environment.get('clientId') + ':' + pm.environment.get('secret'));

pm.sendRequest({
  url: pm.environment.get('baseUrl').replace(/\/$/, '') + '/api/oauth/v1/token',
  method: 'POST',
  header: {
    'Authorization': 'Basic ' + base64ClientIdSecret,
    'Content-Type': 'application/json',
  },
  body: {
    mode: 'raw',
    raw: JSON.stringify(
      {
        'username': pm.environment.get('username'),
        'password': pm.environment.get('password'),
        'grant_type': 'password'
      }
    )
  }
}, function (err, res) {
  if (err !== null) {
    console.error('The following error occured during pre-script execution');
    console.error(err);

    return;
  }
  console.warn('For simplicity, we acquire a new token before each API call, this is not recommended in production. Benefit from the 1 hour validity of the access_token and refresh token mecanism.');
  pm.environment.set('bearerToken', res.json().access_token);
});
